library.background:
/**
 * Distance between two points
 */
float dist(Point p1, Point p2){
    return length(p1.coords - p2.coords);
}

solid.shader:
Point loc = {{name}}.item.loc;
{{#global}}
loc = applyIsometry(v.invCellBoost, loc);
{{/global}}
return dist(v.vec.pos, loc) - {{radius}};

Point loc = {{name}}.item.loc;
{{#global}}
loc = applyIsometry(v.invCellBoost, loc);
{{/global}}
Vector res = Vector(v.vec.pos, v.vec.pos.coords - loc.coords);
res =  geomNormalize(res);
return GenVector(v.cellBoost, v.invCellBoost, res);
